package com.contentsquare.rn.erroranalysis;

import com.contentsquare.android.error.analysis.ErrorAnalysis;
import com.contentsquare.rn.erroranalysis.utils.ConvertReadableArrayToStringList;

import com.facebook.react.bridge.*;

import java.util.List;

import android.util.Log;

public class ErrorAnalysisModule extends ReactContextBaseJavaModule {
    private static final String NAME = "ErrorAnalysisModule";

    public ErrorAnalysisModule(ReactApplicationContext reactContext) {
        super(reactContext);
    }

    @Override
    public String getName() {
        return NAME;
    }

    @ReactMethod
    public void setURLMaskingPatterns(ReadableArray patterns) {
        List<String> stringList = ConvertReadableArrayToStringList.readableArrayToStringList(patterns);
        ErrorAnalysis.setUrlMaskingPatterns(stringList);
    }
}