package com.contentsquare.rn.erroranalysis.utils;

import com.facebook.react.bridge.ReadableArray;

import java.util.ArrayList;
import java.util.List;

public class ConvertReadableArrayToStringList {
    public static List<String> readableArrayToStringList(ReadableArray array) {
        List<String> stringList = new ArrayList<>();
        for (int i = 0; i < array.size(); i++) {
            switch (array.getType(i)) {
                case String:
                    stringList.add(array.getString(i));
                    break;
                case Number:
                    stringList.add(String.valueOf(array.getDouble(i)));
                    break;
                case Boolean:
                    stringList.add(String.valueOf(array.getBoolean(i)));
                    break;
                case Null:
                    stringList.add("");
                    break;
                case Array:
                    stringList.add(readableArrayToStringList(array.getArray(i)).toString());
                    break;
                default:
                    throw new IllegalArgumentException("Unsupported type at index " + i);
            }
        }
        return stringList;
    }
}




