import { ContentsquareModule } from './nativeModules';

/**
 * Returns current session replay link if SR is activated
 * null otherwise
 */
export const getCurrentSessionReplayLink = async () => {
  return new Promise(resolve => {
    ContentsquareModule.getCurrentSessionReplayLink(resolve);
  });
};

/**
 * set the default value of Masking
 * @param {boolean} isMasking default value if not interpreted is TRUE
 */
export const setDefaultMasking = isMasking => {
  ContentsquareModule.setDefaultMasking(isMasking);
};
