import { Platform } from 'react-native';
import { version } from '../package.json';
import { ContentsquareModule } from './nativeModules';

/**
 * Initialization of components
 */
export const initComponents = () => {
  const reactNativeVersion =
    `${Platform.constants.reactNativeVersion.major}.` +
    `${Platform.constants.reactNativeVersion.minor}.` +
    `${Platform.constants.reactNativeVersion.patch}`;

  ContentsquareModule.initComponents({
    xpf_type: 'reactnative',
    xpf_version: reactNativeVersion,
    xpf_bridge_version: version,
  });
};
