import { ContentsquareModule } from './nativeModules';

/**
 * Injects the Contentsquare SDK in the native WebView found at the reference.
 *
 * @param {number} webViewTag the nativeTag of the WebView - from which we will extract the React Tag.
 */
export const injectWebView = webViewTag => {
  ContentsquareModule.injectWebView(webViewTag);
};

/**
 * Removes the Contentsquare SDK injection in the native WebView found at the reference.
 *
 * @param {number} webViewTag the nativeTag of the WebView - from which we will extract the React Tag.
 */
export const removeWebViewInjection = webViewTag => {
  ContentsquareModule.removeWebViewInjection(webViewTag);
};
